package vn.edu.iuh.fit.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.client.RestTemplate;import vn.edu.iuh.fit.entity.Product;import vn.edu.iuh.fit.entity.User;import vn.edu.iuh.fit.repositories.ProductRepository;@RestControllerpublic class ProductControler {    private RestTemplate restTemplate=new RestTemplate();    @Autowired    private ProductRepository productRepository;    @GetMapping("/api/product/{id_user}/{id_product}")    public Product getProductById(@PathVariable("id_user")long id_user,                                  @PathVariable("id_product")long id_product){        User user=restTemplate.getForObject("http://localhost:8080/api/user/"+id_user, User.class);        Product product= productRepository.findById(id_product).get();        product.setUser(user);        return product;    }}