package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;@Entity@Table(name="product")@Data@Getter @Setter@NoArgsConstructor@AllArgsConstructorpublic class Product {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long id;    @Column(name="pro_name")    private String name;    @Column(name="price")    private double price;    @OneToOne    private User user;    public Product(String name, double price) {        this.name = name;        this.price = price;    }    @Override    public String toString() {        return "Product{" +                "id=" + id +                ", name='" + name + '\'' +                ", price=" + price +                ", user=" + user +                '}';    }}