package vn.edu.iuh.fit.entity;import jakarta.persistence.*;import lombok.*;@Entity@Table@Data@Getter@Setter@NoArgsConstructor@AllArgsConstructorpublic class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private long id;    @Column(name="name")    private String name;    @Override    public String toString() {        return "User{" +                "id=" + id +                ", name='" + name + '\'' +                '}';    }}